mapper.select:
SELECT 
FROM practice_records
WHERE user_id = #{userId} AND is_deleted = 0

    AND subject_id = #{subjectId}


    AND question_type = #{questionType}


    AND difficulty = #{difficulty}

ORDER BY create_time DESC

SELECT ar.id, ar.question_id, ar.user_answer, ar.is_correct, ar.answer_time, ar.create_time as answeredAt, q.content as questionContent, q.question_type as questionType, q.difficulty, q.correct_answer as correctAnswer FROM answer_record ar LEFT JOIN question q ON ar.question_id = q.id WHERE ar.user_id = #{userId}
AND ar.practice_type = #{practiceType}
ORDER BY ar.create_time DESC

SELECT ar.id, ar.question_id, ar.user_answer, ar.is_correct, ar.answer_time, ar.create_time as answeredAt, q.content as questionContent, q.question_type as questionType, q.difficulty, q.correct_answer as correctAnswer, q.options, q.analysis, ar.answer_time as timeSpent FROM answer_record ar LEFT JOIN question q ON ar.question_id = q.id WHERE ar.user_id = #{userId} AND ar.practice_type = 1
AND ar.create_time BETWEEN #{startTime} AND #{endTime}
AND ar.create_time >= #{startTime}
ORDER BY ar.create_time ASC
